export const DEFAULT_STATE_TYPE = 'default';
export const DIVIDER_TYPE = 'divider';

export default {
  DEFAULT_DIAGRAM_DIRECTION,
  DEFAULT_NESTED_DOC_DIR,
  STMT_STATE,
