import router from '@/router';
export default {
  created() {
    this.current = this.$store.state.pageState.online;
    this.listOnlineUsers();
  },

  data() {
    return {
      loading: true,
      users: [],
      keywords: null,
      current: 1,
      size: 10,
      total: 0,
      isCheck: false,
      optLog: {}
    };
  },

  methods: {
    listOnlineUsers() {
      this.axios.get('/api/admin/users/online', {
        params: {
          current: this.current,
          size: this.size,
          keywords: this.keywords
        }
      }).then(({
        data
      }) => {
        this.users = data.data.records;
        this.total = data.data.total;
        this.loading = false;
      });
    },

    sizeChange(size) {
      this.size = size;
      this.listOnlineUsers();
    },

    currentChange(current) {
      this.current = current;
      this.$store.commit('updateOnlinePageState', current);
      this.listOnlineUsers();
    },

    removeOnlineUser(user) {
      this.axios.delete('/api/admin/users/' + user.userInfoId + '/online').then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });

          if (user.userInfoId == this.$store.state.userInfo.id) {
            router.push({
              path: '/login'
            });
            sessionStorage.removeItem('token');
          }

          this.listOnlineUsers();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }
      });
    }

  },
  computed: {
    tagType() {
      return function (type) {
        switch (type) {
          case 'GET':
            return '';

          case 'POST':
            return 'success';

          case 'PUT':
            return 'warning';

          case 'DELETE':
            return 'danger';
        }
      };
    }

  }
};
