export default {
  created() {
    this.current = this.$store.state.pageState.operationLog;
    this.listLogs();
  },

  data() {
    return {
      loading: true,
      logs: [],
      logIds: [],
      keywords: null,
      current: 1,
      size: 10,
      total: 0,
      isCheck: false,
      isDelete: false,
      optLog: {}
    };
  },

  methods: {
    selectionChange(logs) {
      this.logIds = [];
      logs.forEach(item => {
        this.logIds.push(item.id);
      });
    },

    searchLogs() {
      this.current = 1;
      this.$store.commit('updateOperationLogPageState', this.current);
      this.listLogs();
    },

    sizeChange(size) {
      this.size = size;
      this.listLogs();
    },

    currentChange(current) {
      this.current = current;
      this.$store.commit('updateOperationLogPageState', current);
      this.listLogs();
    },

    listLogs() {
      this.axios.get('/api/admin/operation/logs', {
        params: {
          current: this.current,
          size: this.size,
          keywords: this.keywords
        }
      }).then(({
        data
      }) => {
        this.logs = data.data.records;
        this.total = data.data.total;
        this.loading = false;
      });
    },

    deleteLog(id) {
      var param = {};

      if (id != null) {
        param = {
          data: [id]
        };
      } else {
        param = {
          data: this.logIds
        };
      }

      this.axios.delete('/api/admin/operation/logs', param).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listLogs();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }

        this.isDelete = false;
      });
    },

    check(optLog) {
      this.optLog = JSON.parse(JSON.stringify(optLog));
      this.isCheck = true;
    }

  },
  computed: {
    tagType() {
      return function (type) {
        switch (type) {
          case 'GET':
            return '';

          case 'POST':
            return 'success';

          case 'PUT':
            return 'warning';

          case 'DELETE':
            return 'danger';
        }
      };
    }

  }
};
