export default {
  created() {
    this.current = this.$store.state.pageState.talkList;
    this.listTalks();
  },

  data: function () {
    return {
      current: 1,
      size: 5,
      count: 0,
      isSecret: null,
      isdelete: false,
      talks: [],
      previews: [],
      talkId: null
    };
  },
  methods: {
    handleCommand(command) {
      var arr = command.split(',');
      this.talkId = arr[1];

      switch (arr[0]) {
        case '1':
          this.$router.push({
            path: '/talks/' + this.talkId
          });
          break;

        case '2':
          this.isdelete = true;
          break;
      }
    },

    listTalks() {
      this.axios.get('/api/admin/talks', {
        params: {
          current: this.current,
          size: this.size,
          isSecret: this.isSecret
        }
      }).then(({
        data
      }) => {
        this.talks = data.data.records;
        this.talks.forEach(item => {
          if (item.imgs) {
            this.previews.push(...item.imgs);
          }
        });
        this.count = data.data.count;
      });
    },

    sizeChange(size) {
      this.previews = [];
      this.size = size;
      this.listTalks();
    },

    currentChange(current) {
      this.previews = [];
      this.current = current;
      this.$store.commit('updateTalkListPageState', current);
      this.listTalks();
    },

    changeStatus(status) {
      this.current = 1;
      this.previews = [];
      this.isSecret = status;
      this.listTalks();
    },

    deleteTalk() {
      this.axios.delete('/api/admin/talks', {
        data: [this.talkId]
      }).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listTalks();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }

        this.isdelete = false;
      });
    }

  },
  computed: {
    isActive() {
      return function (status) {
        return this.isSecret == status ? 'active-status' : 'status';
      };
    }

  }
};
