export default {
  created() {
    this.current = this.$store.state.pageState.category;
    this.listCategories();
  },

  data: function () {
    return {
      isDelete: false,
      loading: true,
      addOrEdit: false,
      keywords: null,
      categoryIds: [],
      categories: [],
      categoryForm: {
        id: null,
        categoryName: '',
        sort: ''
      },
      current: 1,
      size: 10,
      total: 0
    };
  },
  methods: {
    selectionChange(categories) {
      this.categoryIds = [];
      categories.forEach(item => {
        this.categoryIds.push(item.id);
      });
    },

    searchCategories() {
      this.current = 1;
      this.listCategories();
    },

    sizeChange(size) {
      this.size = size;
      this.listCategories();
    },

    currentChange(current) {
      this.current = current;
      this.$store.commit('updateCategoryPageState', current);
      this.listCategories();
    },

    deleteCategory(id) {
      let param = {};

      if (id == null) {
        param = {
          data: this.categoryIds
        };
      } else {
        param = {
          data: [id]
        };
      }

      this.axios.delete('/api/admin/categories', param).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listCategories();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }

        this.isDelete = false;
      });
    },

    listCategories() {
      this.axios.get('/api/admin/categories', {
        params: {
          current: this.current,
          size: this.size,
          keywords: this.keywords
        }
      }).then(({
        data
      }) => {
        this.categories = data.data.records;
        this.total = data.data.total;
        this.loading = false;
      });
    },

    openModel(category) {
      if (category != null) {
        this.categoryForm = JSON.parse(JSON.stringify(category));
        this.$refs.categoryTitle.innerHTML = '修改分类';
      } else {
        this.categoryForm.id = null;
        this.categoryForm.categoryName = '';
        this.$refs.categoryTitle.innerHTML = '添加分类';
      }

      this.addOrEdit = true;
    },

    addOrEditCategory() {
      if (this.categoryForm.categoryName.trim() == '') {
        this.$message.error('分类名不能为空');
        return false;
      }

      if (this.categoryForm.sort < 0 || this.categoryForm.sort > 10 || this.categoryForm.sort % 1 !== 0) {
        this.$message.error('序号只能为0至10的整数');
        return false;
      }

      this.axios.post('/api/admin/categories', this.categoryForm).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listCategories();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }

        this.addOrEdit = false;
      });
    }

  }
};
