export default {
  created() {
    this.listPhotos();
  },

  data: function () {
    return {
      loading: true,
      batchDeletePhoto: false,
      isIndeterminate: false,
      checkAll: false,
      photos: [],
      photoIds: [],
      selectPhotoIds: [],
      current: 1,
      size: 18,
      total: 0
    };
  },
  methods: {
    listPhotos() {
      this.axios.get('/api/admin/photos', {
        params: {
          current: this.current,
          size: this.size,
          isDelete: 1
        }
      }).then(({
        data
      }) => {
        this.photos = data.data.records;
        this.total = data.data.total;
        this.loading = false;
      });
    },

    sizeChange(size) {
      this.size = size;
      this.listPhotos();
    },

    currentChange(current) {
      this.current = current;
      this.listPhotos();
    },

    updatePhotoDelete(id) {
      var param = {};

      if (id == null) {
        param = {
          ids: this.selectPhotoIds,
          isDelete: 0
        };
      } else {
        param = {
          ids: [id],
          isDelete: 0
        };
      }

      this.axios.put('/api/admin/photos/delete', param).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listPhotos();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }
      });
      this.batchDeletePhoto = false;
    },

    deletePhotos() {
      this.axios.delete('/api/admin/photos', {
        data: this.selectPhotoIds
      }).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listPhotos();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }
      });
      this.batchDeletePhoto = false;
    },

    handleCheckAllChange(val) {
      this.selectPhotoIds = val ? this.photoIds : [];
      this.isIndeterminate = false;
    },

    handleCheckedPhotoChange(value) {
      let checkedCount = value.length;
      this.checkAll = checkedCount === this.photoIds.length;
      this.isIndeterminate = checkedCount > 0 && checkedCount < this.photoIds.length;
    }

  },
  watch: {
    photos() {
      this.photoIds = [];
      this.photos.forEach(item => {
        this.photoIds.push(item.id);
      });
    }

  }
};
