export default {
  created() {
    this.current = this.$store.state.pageState.tag;
    this.listTags();
  },

  data: function () {
    return {
      isDelete: false,
      loading: true,
      addOrEdit: false,
      keywords: null,
      tags: [],
      tagIds: [],
      tagForm: {
        id: null,
        tagName: '',
        sort: ''
      },
      current: 1,
      size: 10,
      count: 0
    };
  },
  methods: {
    selectionChange(tags) {
      this.tagIds = [];
      tags.forEach(item => {
        this.tagIds.push(item.id);
      });
    },

    searchTags() {
      this.current = 1;
      this.listTags();
    },

    sizeChange(size) {
      this.size = size;
      this.listTags();
    },

    currentChange(current) {
      this.current = current;
      this.$store.commit('updateTagPageState', current);
      this.listTags();
    },

    deleteTag(id) {
      var param = {};

      if (id == null) {
        param = {
          data: this.tagIds
        };
      } else {
        param = {
          data: [id]
        };
      }

      this.axios.delete('/api/admin/tags', param).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listTags();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }
      });
      this.isDelete = false;
    },

    listTags() {
      this.axios.get('/api/admin/tags', {
        params: {
          current: this.current,
          size: this.size,
          keywords: this.keywords
        }
      }).then(({
        data
      }) => {
        this.tags = data.data.records;
        this.count = data.data.count;
        this.loading = false;
      });
    },

    openModel(tag) {
      if (tag != null) {
        this.tagForm = JSON.parse(JSON.stringify(tag));
        this.$refs.tagTitle.innerHTML = '修改标签';
      } else {
        this.tagForm.id = null;
        this.tagForm.tagName = '';
        this.$refs.tagTitle.innerHTML = '添加标签';
      }

      this.addOrEdit = true;
    },

    addOrEditTag() {
      if (this.tagForm.tagName.trim() == '') {
        this.$message.error('标签名不能为空');
        return false;
      }

      if (this.tagForm.sort < 0 || this.tagForm.sort > 10 || this.tagForm.sort % 1 !== 0) {
        this.$message.error('序号只能为0至10的整数');
        return false;
      }

      this.axios.post('/api/admin/tags', this.tagForm).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listTags();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }
      });
      this.addOrEdit = false;
    }

  }
};
