import * as imageConversion from 'image-conversion';
export default {
  created() {
    this.listAlbums();
  },

  data: function () {
    return {
      keywords: '',
      loading: true,
      isdelete: false,
      addOrEdit: false,
      albumForum: {
        id: null,
        albumName: '',
        albumInfo: '',
        albumCover: '',
        status: 1
      },
      albums: [],
      current: 1,
      size: 8,
      total: 0,
      headers: {
        Authorization: 'Bearer ' + sessionStorage.getItem('token')
      }
    };
  },
  methods: {
    openModel(item) {
      if (item) {
        console.log(item);
        this.albumForum = JSON.parse(item);
        this.$refs.albumTitle.innerHTML = '修改相册';
      } else {
        this.albumForum = {
          id: null,
          albumName: '',
          albumLabel: '',
          albumCover: '',
          status: 1
        };
        this.$refs.albumTitle.innerHTML = '新建相册';
      }

      this.addOrEdit = true;
    },

    checkPhoto(item) {
      this.$router.push({
        path: '/albums/' + item.id
      });
    },

    checkDelete() {
      this.$router.push({
        path: '/photos/delete'
      });
    },

    listAlbums() {
      this.axios.get('/api/admin/photos/albums', {
        params: {
          current: this.current,
          size: this.size,
          keywords: this.keywords
        }
      }).then(({
        data
      }) => {
        this.albums = data.data.records;
        this.total = data.data.total;
        this.loading = false;
      });
    },

    addOrEditAlbum() {
      if (this.albumForum.albumName.trim() == '') {
        this.$message.error('相册名称不能为空');
        return false;
      }

      if (this.albumForum.albumInfo.trim() == '') {
        this.$message.error('相册描述不能为空');
        return false;
      }

      if (this.albumForum.albumCover == null) {
        this.$message.error('相册封面不能为空');
        return false;
      }

      this.axios.post('/api/admin/photos/albums', this.albumForum).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listAlbums();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }
      });
      this.addOrEdit = false;
    },

    uploadCover(response) {
      this.albumForum.albumCover = response.data;
    },

    beforeUpload(file) {
      return new Promise(resolve => {
        if (file.size / 1024 < this.config.UPLOAD_SIZE) {
          resolve(file);
        }

        imageConversion.compressAccurately(file, this.config.UPLOAD_SIZE).then(res => {
          resolve(res);
        });
      });
    },

    handleCommand(command) {
      const type = command.substring(0, 6);
      const data = command.substring(6);

      if (type == 'delete') {
        this.albumForum.id = data;
        this.isdelete = true;
      } else {
        console.log(data);
        this.openModel(data);
      }
    },

    deleteAlbum() {
      this.axios.delete('/api/admin/photos/albums/' + this.albumForum.id).then(({
        data
      }) => {
        if (data.code === 0) {
          this.$notify.success({
            title: '成功',
            message: data.message
          });
          this.listAlbums();
        } else {
          this.$notify.error({
            title: '失败',
            message: data.message
          });
        }

        this.isdelete = false;
      });
    },

    searchAlbums() {
      this.current = 1;
      this.listAlbums();
    },

    sizeChange(size) {
      this.size = size;
      this.listAlbums();
    },

    currentChange(current) {
      this.current = current;
      this.listAlbums();
    }

  }
};
